template.value:
add_filter( 'body_class', '$prefix$_body_class' );
/**
* Adds a css class to the body element
*
* @param  array $classes the current body classes
* @return array $classes modified classes
*/
function $prefix$_body_class( $classes ) {
    $classes[] = '$className$';
    return $classes;
}

//* Add the home featured section
add_action( '$genesis_hook$', '$wpbasics_my_function$' );

function $wpbasics_my_function$() {

	if (is_active_sidebar( '$widgetID$' )) {

		genesis_widget_area( '$widgetID$', array(

			'before' => '<div class="$widgetID$ widget-area"><div class="wrap">',

			'after' => '</div></div>'

		) );

	}

}

remove_action( 'genesis_loop', 'genesis_do_loop' );
add_action( 'genesis_loop', '$prefix$_do_loop' );
/**
* Outputs a custom loop
*
* @global mixed $paged current page number if paginated
* @return void
*/
function $prefix$_do_loop() {

    global $paged;

    // accepts any wp_query args
    $args = (array(
        'post_type'      => '',
        'category_name'  => 'blog', // use category slug
        'order'          => 'ASC',
        'orderby'       => 'title',
        'paged'          => $paged,
        'posts_per_page' => 5
        ));

    genesis_custom_loop( $args );
}

// Change the footer text
// http://my.studiopress.com/docs/shortcode-reference/#footer-shortcodes
add_filter( 'genesis_footer_creds_text', '$io_footer_creds_filter$' );
function $io_footer_creds_filter$( $creds ) {
	$creds = 'Copyright [footer_copyright first="2016"] &middot; <a href="'.get_bloginfo( 'url' ).'">My Custom Link</a> &middot; Built on the <a href="http://www.studiopress.com/themes/genesis" title="Genesis Framework">Genesis Framework</a>';
	return $creds;
}

//* Customize the entry meta in the entry footer (requires HTML5 theme support)
add_filter( 'genesis_post_meta', 'sp_post_meta_filter' );
function sp_post_meta_filter($post_meta) {
	$post_meta = '[post_categories] [post_tags]';
	return $post_meta;
}

// Customize entry meta in the entry header
add_filter( 'genesis_post_info', 'sp_post_info_filter' );
function sp_post_info_filter( $post_info ) {
    $post_info = '[post_date] by [post_author_posts_link] [post_comments] [post_edit]';
    return $post_info;
}

add_filter( 'wp_nav_menu_items', 'theme_menu_extras', 10, 2 );
/**
* Filter menu items, appending either a search form or today's date.
*
* @param string   $menu HTML string of list items.
* @param stdClass $args Menu arguments.
*
* @return string Amended HTML string of list items.
*/
function theme_menu_extras( $menu, $args ) {
    // Change 'primary' to 'secondary' to add extras to the secondary navigation menu
    if ( 'primary' !== $args->theme_location )
        return $menu;

    // Uncomment this block to add a search form to the navigation menu
    /*
    ob_start();
    get_search_form();
    $search = ob_get_clean();
    $menu  .= '<li class="right search">' . $search . '</li>';
    */

    // Uncomment this block to add the date to the navigation menu
    /*
    $menu .= '<li class="right date">' . date_i18n( get_option( 'date_format' ) ) . '</li>';
    */

    return $menu;
}

/**
* Template Redirect
* Use $template_category_grid_php$ for $recipes$_category_archive.*/

add_filter( 'template_include', '$custom_category_template$', 99 );
function $custom_category_template$( $template ) {

    if ( is_category( '$recipes$' ) ) {
        $new_template = locate_template( array( '$template_category_grid_php$' ) );
        if ( '' != $new_template ) {
            return $new_template ;
        }
    }

    return $template;
}
